    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        double init;
        double rate;
        double time;
        double interest;
        double total;
        init = Double.parseDouble(PV.getText());
        rate = Double.parseDouble(RV.getText());
        time = Double.parseDouble(TV.getText());
        interest = ((init*rate*time)/100);
        total = interest+init;
        if(rate<=0 || rate>=100){
            err.setText("Error 0x0082193");
        }else if(time>50||time<0){
            err.setText("Error 0x0083810");
        }else if(init<=0){
            err.setText("Error 0x0092379");
        }else{
        IV.setText(Double.toString(interest));
        AV.setText(Double.toString(total));
        }
    }//GEN-LAST:event_jButton1ActionPerformed

