    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        double init;
        double rate;
        double time;
        double num;
        double interest;
        double totala;
        double totalb;
        double totalc;
        double total;
        init = Double.parseDouble(PPV.getText());
        rate = Double.parseDouble(RRV.getText());
        time = Double.parseDouble(TTV.getText());
        num = Double.parseDouble(NNV.getText());
        if(rate<=0 || rate>=100){
            er.setText("Error 0x0082193");
        }else if(time>50||time<0){
            er.setText("Error 0x0083810");
        }else if(init<=0){
            er.setText("Error 0x0092379");
        }else if(num<=0||num>366){
            er.setText("Error 0x0006578");
        }else{
        totala = ((1+(rate/100)));
        totalb = num*time;
        totalc = Math.pow(totala, totalb);
        total = init*totalc;
        interest = total-init;
        IIV.setText(Double.toString(interest));
        AAV.setText(Double.toString(total));
        }
    }//GEN-LAST:event_jButton3ActionPerformed

